using System;\nusing System.Data;\nusing System.Data.Odbc;\n\nclass ClobInsertExample\n{\n    public static void InsertClob(string connectionString, string tableName, string clobData)\n    {\n        using (OdbcConnection connection = new OdbcConnection(connectionString))\n        {\n            connection.Open();\n\n            string query = $"INSERT INTO {tableName} (clob_column) VALUES (?)";\n\n            OdbcCommand command = new OdbcCommand(query, connection);\n\n            OdbcParameter parameter = new OdbcParameter();\n            parameter.ParameterName = "clob_parameter";\n            parameter.OdbcType = OdbcType.Text;\n            parameter.Value = clobData;\n\n            command.Parameters.Add(parameter);\n\n            command.ExecuteNonQuery();\n        }\n    }\n}