using System.Diagnostics.Eventing.Reader;\n\nEventLogQuery query = new EventLogQuery("path/to/archived/eventlog.evtx", PathType.FilePath);\nEventLogReader reader = new EventLogReader(query);\n\nforeach (EventRecord record in reader)\n{\n    Console.WriteLine(record.Id);\n    Console.WriteLine(record.LogName);\n    Console.WriteLine(record.LevelDisplayName);\n    Console.WriteLine(record.FormatDescription());\n}