public class ArrayXPathNodeIterator : XPathNodeIterator\n{\n    private IXPathNavigable[] _array;\n    private int _index;\n\n    public ArrayXPathNodeIterator(IXPathNavigable[] array)\n    {\n        _array = array;\n        _index = 0;\n    }\n\n    public override bool MoveNext()\n    {\n        if (_index < _array.Length)\n        {\n            _index++;\n            return true;\n        }\n        return false;\n    }\n\n    public override XPathNavigator Current\n    {\n        get { return _array[_index - 1].CreateNavigator(); }\n    }\n\n    public override int Count\n    {\n        get { return _array.Length; }\n    }\n}