// Data layer wrapper\npublic class DataWrapper\n{\n    private DataLayer _dataLayer;\n\n    public DataWrapper(DataLayer dataLayer)\n    {\n        _dataLayer = dataLayer;\n    }\n\n    public ICollection<MyDataObject> GetData()\n    {\n        DataSet dataSet = _dataLayer.GetData();\n        List<MyDataObject> dataObjects = new List<MyDataObject>();\n\n        foreach (DataRow row in dataSet.Tables[0].Rows)\n        {\n            MyDataObject dataObject = new MyDataObject();\n            dataObject.Id = Convert.ToInt32(row["Id"]);\n            dataObject.Name = row["Name"].ToString();\n            // ...\n\n            dataObjects.Add(dataObject);\n        }\n\n        return dataObjects;\n    }\n}\n\n// Strongly-typed data object\npublic class MyDataObject\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    // ...\n}