using Microsoft.Web.Administration;\n\n// ...\n\nbool isModulePresent = false;\n\nusing (ServerManager serverManager = new ServerManager())\n{\n    Configuration config = serverManager.GetApplicationHostConfiguration();\n\n    ConfigurationSection httpModulesSection = config.GetSection("system.web/httpModules");\n    if (httpModulesSection != null)\n    {\n        foreach (HttpModuleAction module in httpModulesSection.GetCollection())\n        {\n            if (module.Type == typeof(MyModule).FullName)\n            {\n                isModulePresent = true;\n                break;\n            }\n        }\n    }\n\n    ConfigurationSection modulesSection = config.GetSection("system.webServer/modules");\n    if (modulesSection != null)\n    {\n        foreach (ModuleAction module in modulesSection.GetCollection())\n        {\n            if (module.Type == typeof(MyModule).FullName)\n            {\n                isModulePresent = true;\n                break;\n            }\n        }\n    }\n}