[WebMethod]\npublic static string GetTelephone(int personId)\n{\n    // Retrieve telephone number from database or other source\n    string telephone = GetTelephoneFromDatabase(personId);\n    // Store telephone number in Session\n    HttpContext.Current.Session["telephone"] = telephone;\n    return telephone;\n}\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (Session["telephone"] != null)\n    {\n        Label1.Text = Session["telephone"].ToString();\n    }\n}