protected void Page_Load(object sender, EventArgs e)\n{\n    if (ViewState["telephone"] != null)\n    {\n        Label1.Text = ViewState["telephone"].ToString();\n    }\n}\n\n[WebMethod]\npublic static string GetTelephone(int personId)\n{\n    // Retrieve telephone number from database or other source\n    string telephone = GetTelephoneFromDatabase(personId);\n    // Store telephone number in ViewState\n    HttpContext.Current.ViewState["telephone"] = telephone;\n    return telephone;\n}