using System.Security.Cryptography;\n\npublic static string GenerateKey(RSA rsa)\n{\n    var randomBytes = new byte[16];\n    using var rng = RandomNumberGenerator.Create();\n    rng.GetBytes(randomBytes);\n    return Convert.ToBase64String(rsa.Encrypt(randomBytes, RSAEncryptionPadding.OaepSHA256));\n}\n\npublic static bool VerifyKey(RSA rsa, string key)\n{\n    try\n    {\n        var decryptedBytes = rsa.Decrypt(Convert.FromBase64String(key), RSAEncryptionPadding.OaepSHA256);\n        return true;\n    }\n    catch (CryptographicException)\n    {\n        return false;\n    }\n}