using ClamAVSharp;\n\n// Create a new ClamAV engine instance\nClamEngine engine = new ClamEngine();\n\n// Set the path to the ClamAV database\nengine.DatabasePath = @"C:\Path\To\ClamAV\Database";\n\n// Scan a file\nScanResult result = engine.ScanFile(@"C:\Path\To\File.exe");\n\n// Check if the file is infected\nif (result.Infected)\n{\n    Console.WriteLine("File is infected with malware!");\n}