using System;\nusing System.Reflection;\n\npublic class WebServerChecker\n{\n    public static bool IsRunningUnderWebServer()\n    {\n        try\n        {\n            // Try to load the System.Web assembly\n            Assembly webAssembly = Assembly.Load("System.Web");\n            // If the assembly is loaded, check if the ProvidersHelper class exists\n            Type providersHelperType = webAssembly.GetType("System.Web.Configuration.ProvidersHelper");\n            return providersHelperType != null;\n        }\n        catch (Exception)\n        {\n            // If the assembly cannot be loaded or the class is not found, return false\n            return false;\n        }\n    }\n}