using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // define the local time zone\n        TimeZoneInfo localTimeZone = TimeZoneInfo.FindSystemTimeZoneById("Eastern Standard Time");\n\n        // define the local date\n        DateTime localDate = new DateTime(2022, 1, 1, 12, 0, 0);\n\n        // convert to UTC\n        DateTime utcDate = TimeZoneInfo.ConvertTimeToUtc(localDate, localTimeZone);\n\n        Console.WriteLine($"Local Date: {localDate}");\n        Console.WriteLine($"UTC Date: {utcDate}");\n    }\n}