using Newtonsoft.Json;\n\npublic class Level\n{\n    public string Name { get; set; }\n    public List<GameObject> Objects { get; set; }\n    public List<Terrain> Terrains { get; set; }\n}\n\npublic class GameObject\n{\n    public string Type { get; set; }\n    public Vector3 Position { get; set; }\n}\n\npublic class Terrain\n{\n    public string Type { get; set; }\n    public Vector3 Position { get; set; }\n}\n\n// Create a level instance\nLevel level = new Level\n{\n    Name = "My Level",\n    Objects = new List<GameObject>\n    {\n        new GameObject { Type = "Enemy", Position = new Vector3(1, 2, 3) },\n        new GameObject { Type = "PowerUp", Position = new Vector3(4, 5, 6) }\n    },\n    Terrains = new List<Terrain>\n    {\n        new Terrain { Type = "Grass", Position = new Vector3(0, 0, 0) },\n        new Terrain { Type = "Dirt", Position = new Vector3(1, 1, 1) }\n    }\n};\n\n// Serialize the level to JSON\nstring json = JsonConvert.SerializeObject(level, Formatting.Indented);\n\n// Save the JSON to a file\nFile.WriteAllText("level.json", json);