private readonly object mylock = new object();\n\npublic void AddToFileCollection()\n{\n    lock (mylock)\n    {\n        // Add to collection\n        Console.WriteLine("Adding to collection...");\n        Thread.Sleep(1000); // Simulate some work\n        Console.WriteLine("Added to collection.");\n    }\n}\n\npublic void RemoveFromFileCollection()\n{\n    lock (mylock)\n    {\n        // Remove from collection\n        Console.WriteLine("Removing from collection...");\n        Thread.Sleep(500); // Simulate some work\n        Console.WriteLine("Removed from collection.");\n    }\n}