public class CustomMembershipProvider : SqlMembershipProvider\n{\n    public override void Initialize(string name, NameValueCollection config)\n    {\n        base.Initialize(name, config);\n\n        // Load settings from database\n        string connectionString = ConfigurationManager.ConnectionStrings["YourConnectionString"].ConnectionString;\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            SqlCommand command = new SqlCommand("SELECT MaxInvalidPasswordAttempts, PasswordAttemptWindow FROM YourSettingsTable", connection);\n            SqlDataReader reader = command.ExecuteReader();\n\n            while (reader.Read())\n            {\n                MaxInvalidPasswordAttempts = (int)reader["MaxInvalidPasswordAttempts"];\n                PasswordAttemptWindow = (int)reader["PasswordAttemptWindow"];\n            }\n        }\n    }\n}