using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\n// Encryption\npublic string EncryptConnectionString(string connectionString)\n{\n    using (var rsa = RSA.Create())\n    {\n        rsa.ImportProtectedData( /* load public key */ );\n        var encryptedBytes = rsa.Encrypt(Encoding.UTF8.GetBytes(connectionString), RSAEncryptionPadding.OaepSHA256);\n        return Convert.ToBase64String(encryptedBytes);\n    }\n}\n\n// Decryption\npublic string DecryptConnectionString(string encryptedConnectionString)\n{\n    using (var rsa = RSA.Create())\n    {\n        rsa.ImportProtectedData( /* load private key */ );\n        var encryptedBytes = Convert.FromBase64String(encryptedConnectionString);\n        var decryptedBytes = rsa.Decrypt(encryptedBytes, RSAEncryptionPadding.OaepSHA256);\n        return Encoding.UTF8.GetString(decryptedBytes);\n    }\n}