using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        var query = "SELECT * FROM Win32_MappedLogicalDisk";\n        var searcher = new ManagementObjectSearcher(query);\n\n        foreach (ManagementObject disk in searcher.Get())\n        {\n            Console.WriteLine($"Drive: {disk["DeviceID"]}, Label: {disk["VolumeName"]}");\n        }\n    }\n}