using System.Data;\nusing Oracle.DataAccess.Client;\n\n// Create a new OleDb connection\nOracleConnection conn = new OracleConnection("Data Source=oracle_server;User Id(username);Password(password);");\n\n// Create a new OleDb command\nOracleCommand cmd = new OracleCommand("SELECT * FROM table_name", conn);\n\n// Open the connection and execute the command\nconn.Open();\nOracleDataReader reader = cmd.ExecuteReader();\n\n// Process the data\nwhile (reader.Read())\n{\n    // Do something with the data\n}\n\n// Close the connection\nconn.Close();