public bool ValidateSource(Source srcToValidate)\n{\n    // Custom validation rule: check if URL is valid and exists in database\n    if (!Uri.TryCreate(srcToValidate.Url, UriKind.Absolute, out Uri uri))\n    {\n        _validationDictionary.AddError("Url", "Invalid URL.");\n    }\n    else\n    {\n        if (!Database.UrlExists(uri))\n        {\n            _validationDictionary.AddError("Url", "URL does not exist in database.");\n        }\n    }\n    // ...\n}