using System.Data.Linq;\n\n// Create a DataContext instance\nvar context = new DataContext(MyConsoleApp.Properties.Settings.Default.ConnStr);\n\n// Read the SQL query from the console\nstring query = Console.ReadLine();\n\n// Execute the query and retrieve the results\nvar results = context.ExecuteQuery<object>(query);\n\n// Print the results\nforeach (var row in results)\n{\n    var properties = row.GetType().GetProperties();\n    foreach (var property in properties)\n    {\n        Console.Write("| {0}: {1}", property.Name, property.GetValue(row));\n    }\n    Console.WriteLine();\n}