using System;\nusing System.IO;\nusing System.Threading;\n\nclass LogFileReader\n{\n    private string _logFilePath;\n    private long _lastReadPosition;\n\n    public LogFileReader(string logFilePath)\n    {\n        _logFilePath = logFilePath;\n        _lastReadPosition = 0;\n    }\n\n    public void ReadLogFile()\n    {\n        using (FileStream fs = new FileStream(_logFilePath, FileMode.Open, FileAccess.Read, FileShare.ReadWrite))\n        {\n            fs.Seek(_lastReadPosition, SeekOrigin.Begin);\n\n            using (StreamReader sr = new StreamReader(fs))\n            {\n                string line;\n                while ((line = sr.ReadLine()) != null)\n                {\n                    Console.WriteLine(line);\n                }\n\n                _lastReadPosition = fs.Position;\n            }\n        }\n    }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        LogFileReader reader = new LogFileReader("path_to_your_log_file.log");\n\n        while (true)\n        {\n            reader.ReadLogFile();\n            Thread.Sleep(1000); // wait 1 second\n        }\n    }\n}