using MailKit.Net.Smtp;\nusing MailKit.Security;\nusing MimeKit;\n\n// Create a new email message\nvar message = new MimeMessage();\nmessage.From.Add(new MailboxAddress("Your Name", "your_email@example.com"));\nmessage.To.Add(new MailboxAddress("Recipient Name", "recipient_email@example.com"));\nmessage.Subject = "Test Email";\nmessage.Body = new TextPart("plain") { Text = "This is a test email." };\n\n// Set up the SMTP client\nusing (var client = new SmtpClient())\n{\n    client.Connect("smtp.example.com", 587, SecureSocketOptions.StartTls);\n    client.Authenticate("your_email@example.com", "your_password");\n\n    // Send the email\n    client.Send(message);\n\n    // Disconnect\n    client.Disconnect(true);\n}