using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string drivePath = @"C:\"; // Replace with your drive path\n        string outputFile = @"output.txt"; // Replace with your desired output file\n\n        // Scan the drive and write to output file\n        ScanDrive(drivePath, outputFile);\n    }\n\n    static void ScanDrive(string path, string outputFile)\n    {\n        using (StreamWriter writer = new StreamWriter(outputFile))\n        {\n            ScanDirectory(path, writer, 0);\n        }\n    }\n\n    static void ScanDirectory(string path, StreamWriter writer, int level)\n    {\n        // Write the current directory\n        writer.WriteLine(new string(' ', level * 4) + path);\n\n        // Enumerate files in the current directory\n        foreach (string file in Directory.GetFiles(path))\n        {\n            writer.WriteLine(new string(' ', level * 4) + "  " + Path.GetFileName(file));\n        }\n\n        // Recursively scan subdirectories\n        foreach (string dir in Directory.GetDirectories(path))\n        {\n            ScanDirectory(dir, writer, level + 1);\n        }\n    }\n}