using DiffPlex.DiffBuilder;\nusing DiffPlex.DiffBuilder.Model;\n\n// Load the two text files\nstring file1 = File.ReadAllText(@"output1.txt");\nstring file2 = File.ReadAllText(@"output2.txt");\n\n// Create a diff builder\nvar diffBuilder = new SideBySideDiffBuilder(new Differ());\n\n// Compare the two files\nvar diff = diffBuilder.BuildDiffModel(file1, file2);\n\n// Report changes\nforeach (var line in diff.OldText.Lines)\n{\n    if (line.Type == ChangeType.Deleted)\n    {\n        Console.WriteLine($"Deleted: {line.Text}");\n    }\n}\n\nforeach (var line in diff.NewText.Lines)\n{\n    if (line.Type == ChangeType.Inserted)\n    {\n        Console.WriteLine($"Added: {line.Text}");\n    }\n}