using System.Web.Caching;\n\n// ...\n\nprotected void Application_BeginRequest(Object sender, EventArgs e)\n{\n    if (IsCategoryUrl())\n    {\n        string CategoryName = ParseCategoryNameFromUrl(Request.Url);\n        string CategoryId = GetCategoryIdFromCache(CategoryName);\n        if (CategoryId == null)\n        {\n            CategoryId = GetCategoryIdByNameFromDB(CategoryName);\n            CacheCategoryID(CategoryName, CategoryId);\n        }\n        Context.RewritePath("/Category.aspx?id=" + CategoryId);\n        Response.TrySkipIisCustomErrors = true;\n    }\n}\n\nprivate string GetCategoryIdFromCache(string CategoryName)\n{\n    return (string)HttpContext.Current.Cache[CategoryName];\n}\n\nprivate void CacheCategoryID(string CategoryName, string CategoryId)\n{\n    HttpContext.Current.Cache.Insert(CategoryName, CategoryId, null, DateTime.Now.AddHours(1), TimeSpan.Zero);\n}