using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class TimeSlot\n{\n    public string StartTime { get; set; }\n    public string EndTime { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<TimeSlot> timeSlots = new List<TimeSlot>\n        {\n            new TimeSlot { StartTime = "1200", EndTime = "1300" },\n            new TimeSlot { StartTime = "1300", EndTime = "1400" },\n            new TimeSlot { StartTime = "1400", EndTime = "1500" },\n        };\n\n        List<TimeSlot> orderedTimeSlots = timeSlots.OrderBy(ts => ts.StartTime).ToList();\n\n        foreach (var timeSlot in orderedTimeSlots)\n        {\n            string startTime12Hour = ConvertTo12HourFormat(timeSlot.StartTime);\n            string endTime12Hour = ConvertTo12HourFormat(timeSlot.EndTime);\n\n            Console.WriteLine($"{startTime12Hour} - {endTime12Hour}");\n        }\n    }\n\n    public static string ConvertTo12HourFormat(string time)\n    {\n        DateTime dt = DateTime.ParseExact(time, "HHmm", null);\n        return dt.ToString("ht");\n    }\n}