// Example of proper security measure: encryption\nusing System.Security.Cryptography;\n\npublic class User\n{\n    private string _encryptedSecret;\n\n    public User(string secret)\n    {\n        using (var aes = Aes.Create())\n        {\n            var encryptedBytes = aes.EncryptString(secret);\n            _encryptedSecret = Convert.ToBase64String(encryptedBytes);\n        }\n    }\n\n    // ...\n}