using System;\nusing System.Net.NetworkInformation;\n\npublic class NetworkSpeed\n{\n    public static void Main()\n    {\n        var interfaces = NetworkInterface.GetAllNetworkInterfaces();\n\n        foreach (var networkInterface in interfaces)\n        {\n            if (networkInterface.OperationalStatus == OperationalStatus.Up)\n            {\n                var stats = networkInterface.GetIPv4Statistics();\n\n                Console.WriteLine($"Interface: {networkInterface.Name}");\n                Console.WriteLine($"Speed: {networkInterface.Speed / 1000000} Mbps");\n                Console.WriteLine($"Bytes Sent: {stats.BytesSent}");\n                Console.WriteLine($"Bytes Received: {stats.BytesReceived}");\n                Console.WriteLine();\n            }\n        }\n    }\n}