protected void Page_Load(object sender, EventArgs e)\n{\n    // Get the screen configuration from the cache\n    Screen screen = Cache["ScreenConfig"] as Screen;\n\n    if (screen == null)\n    {\n        // Get the screen configuration from the database\n        screen = GetScreenFromDatabase();\n\n        // Cache the screen configuration\n        Cache["ScreenConfig"] = screen;\n    }\n\n    // Render the controls on the page...\n}