using System;\nusing System.IO;\nusing System.Data;\n\nclass LoadFileIntoDataSet\n{\n    public static void Main()\n    {\n        // Specify the path to your file\n        string filePath = @"path/to/your/file.txt";\n        \n        // Specify the delimiter used in your file (e.g., ",", ";")\n        char delimiter = ',';\n        \n        // Create a new DataSet\n        DataSet dataSet = new DataSet();\n        \n        // Create a new DataTable within the DataSet\n        DataTable dataTable = dataSet.Tables.Add("MyTable");\n        \n        // Define your columns\n        dataTable.Columns.Add("Column1", typeof(string));\n        dataTable.Columns.Add("Column2", typeof(string));\n        \n        // Load the file into the DataTable\n        using (StreamReader reader = new StreamReader(filePath))\n        {\n            string line;\n            while ((line = reader.ReadLine()) != null)\n            {\n                string[] values = line.Split(delimiter);\n                dataTable.Rows.Add(values);\n            }\n        }\n        \n        // Example: Print the DataTable\n        foreach (DataRow row in dataTable.Rows)\n        {\n            foreach (DataColumn column in dataTable.Columns)\n            {\n                Console.Write(row[column].ToString() + " ");\n            }\n            Console.WriteLine();\n        }\n    }\n}