private void aboutButton_Click(object sender, EventArgs e)\n{\n    aboutTextBox.Text = $"Version: {Assembly.GetExecutingAssembly().GetName().Version}\n" +\n                        $"Author: Your Name\n" +\n                        $"Year: 2009\n" +\n                        $"Copyright: Your Company Name";\n\n    // Optional: display additional debug info\n    if (debugMode)\n    {\n        debugInfoButton.Visible = true;\n    }\n}\n\nprivate void debugInfoButton_Click(object sender, EventArgs e)\n{\n    debugInfoTextBox.Text = $"OS Version: {Environment.OSVersion}\n" +\n                            $".NET Framework Version: {Environment.Version}\n" +\n                            $"Assembly Build Date: {Assembly.GetExecutingAssembly().GetCustomAttributes(typeof(AssemblyFileInfoAttribute), false).OfType<AssemblyFileInfoAttribute>().FirstOrDefault()?.Value}";\n}