using System.Globalization;\n\n// Get the current culture\nCultureInfo culture = CultureInfo.CurrentCulture;\n\n// Get the decimal separator and thousands separator\nstring decimalSeparator = culture.NumberFormat.NumberDecimalSeparator;\nstring thousandsSeparator = culture.NumberFormat.NumberGroupSeparator;\n\n// Build a regex pattern using the separators\nstring pattern = $"^[0-9{{0,5}}]{thousandsSeparator}?[0-9{{0,3}}]?({decimalSeparator}[0-9{{0,4}}])?$";\n\n// Use the pattern for text validation\nRegex regex = new Regex(pattern);\nbool isValid = regex.IsMatch(inputText);