using Oracle.ManagedDataAccess.Client;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a connection to the Oracle database\n        string connectionString = "Data Source=(DESCRIPTION=(ADDRESS=(PROTOCOL=TCP)(HOST=your_host)(PORT=your_port))(CONNECT_DATA=(SID=your_sid)));User Id=your_username;Password=your_password;";\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n\n            // Create a command to retrieve the stored procedure metadata\n            OracleCommand command = new OracleCommand("SELECT * FROM ALL_PROCEDURES WHERE OWNER = 'DEV'", connection);\n            OracleDataReader reader = command.ExecuteReader();\n\n            // Generate C# code for each stored procedure\n            while (reader.Read())\n            {\n                string packageName = reader["PACKAGE_NAME"].ToString();\n                string procedureName = reader["PROCEDURE_NAME"].ToString();\n\n                Console.WriteLine($"// {packageName}.{procedureName}");\n\n                // Get the parameter metadata\n                OracleCommand paramCommand = new OracleCommand($"SELECT * FROM ALL_ARGUMENTS WHERE PACKAGE_NAME = '{packageName}' AND PROCEDURE_NAME = '{procedureName}'", connection);\n                OracleDataReader paramReader = paramCommand.ExecuteReader();\n\n                // Generate the C# method signature\n                Console.Write($"public static {reader["RETURN_TYPE"].ToString()} {procedureName}(");\n                bool isFirstParam = true;\n                while (paramReader.Read())\n                {\n                    if (!isFirstParam)\n                    {\n                        Console.Write(", ");\n                    }\n                    Console.Write($"{paramReader["ARGUMENT_NAME"].ToString()} {paramReader["DATA_TYPE"].ToString()}");\n                    isFirstParam = false;\n                }\n                Console.WriteLine(")");\n\n                // Generate the C# method body\n                Console.WriteLine("{");\n                Console.WriteLine($"    using (OracleConnection connection = new OracleConnection(connectionString))");\n                Console.WriteLine($"    {{");\n                Console.WriteLine($"        connection.Open();");\n                Console.WriteLine($"        OracleCommand command = new OracleCommand(\"{packageName}.{procedureName}\", connection);");\n                foreach (OracleParameter param in command.Parameters)\n                {\n                    Console.WriteLine($"        command.Parameters.Add(new OracleParameter(\"{param.ParameterName}\", OracleDbType.{param.OracleDbType}));");\n                }\n                Console.WriteLine($"        return command.ExecuteScalar();");\n                Console.WriteLine($"    }}");\n                Console.WriteLine("}");\n            }\n        }\n    }\n}