protected void Page_Load(object sender, EventArgs e)\n{\n    // Fetch data from database\n    DataTable dt = YourDataAccessLayer.GetData();\n\n    // Bind data to DataList1\n    DataList1.DataSource = dt;\n    DataList1.DataBind();\n}\n\nprotected DataTable GetCities(object dataItem)\n{\n    // Get the country name from the current data item\n    string country = DataBinder.GetPropertyValue(dataItem, "Country").ToString();\n\n    // Filter the cities for the current country\n    DataTable cities = YourDataAccessLayer.GetCities(country);\n\n    return cities;\n}