using System;\nusing System.Xml;\nusing System.Xml.Xsl;\n\npublic class XmlToCsvConverter\n{\n  public static string ConvertXmlToCsv(string xmlInput, string xsltPath)\n  {\n    // Load the XSLT transformation\n    XslCompiledTransform xslt = new XslCompiledTransform();\n    xslt.Load(xsltPath);\n    \n    // Load the input XML\n    XmlDocument xmlDoc = new XmlDocument();\n    xmlDoc.LoadXml(xmlInput);\n    \n    // Perform the XSLT transformation\n    StringWriter csvOutput = new StringWriter();\n    xslt.Transform(xmlDoc, null, csvOutput);\n    \n    // Return the CSV output\n    return csvOutput.ToString();\n  }\n}