public string SEO_makeTitle(object objTitle)\n{\n    if (objTitle == null)\n    {\n        throw new ArgumentNullException(nameof(objTitle));\n    }\n\n    string strTitle = Convert.ToString(objTitle);\n\n    // Use a more specific character class to match only ASCII special characters\n    strTitle = Regex.Replace(strTitle.Trim(), @"[^a-zA-Z0-9\s]", "");\n    strTitle = Regex.Replace(strTitle.Trim(), @"\s{2,}", " ");\n    strTitle = strTitle.Trim().Replace(" ", "-").ToLower();\n\n    return strTitle;\n}