using DocumentFormat.OpenXml;\nusing DocumentFormat.OpenXml.Packaging;\nusing DocumentFormat.OpenXml.Spreadsheet;\n\npublic static void UpdateCell(string docName, string text, uint rowIndex, string columnName)\n{\n    // Open the document for editing.\n    using (SpreadsheetDocument spreadSheet = SpreadsheetDocument.Open(docName, true))\n    {\n        Workbook workBook = spreadSheet.WorkbookPart.Workbook;\n\n        // Get the worksheet part by name (e.g., "Sheet1")\n        WorksheetPart worksheetPart = workBook.WorkbookPart.WorksheetParts\n            .FirstOrDefault(wp => wp.Worksheet.Name == "Sheet1");\n\n        if (worksheetPart != null)\n        {\n            SheetData sheetData = worksheetPart.Worksheet.GetFirstChild<SheetData>();\n\n            // Find the row with the specified row index\n            Row row = sheetData.Elements<Row>().FirstOrDefault(r => r.RowIndex == rowIndex);\n\n            if (row != null)\n            {\n                // Find the cell with the specified column name\n                Cell cell = row.Elements<Cell>().FirstOrDefault(c => c.CellReference == columnName + rowIndex);\n\n                if (cell != null)\n                {\n                    // Update the cell value\n                    cell.CellValue = new CellValue(text);\n                    cell.DataType = CellValues.String;\n                }\n            }\n        }\n    }\n}