private static object locker = new object();\n\npublic SomeData GetData()\n{\n    string cacheKey = "your_cache_key";\n\n    // First check without locking\n    SomeData data = HttpRuntime.Cache[cacheKey] as SomeData;\n    if (data != null)\n    {\n        return data.Copy();\n    }\n\n    // If the data is not in the cache, lock and check again\n    lock (locker)\n    {\n        // Check again after acquiring the lock\n        data = HttpRuntime.Cache[cacheKey] as SomeData;\n        if (data != null)\n        {\n            return data.Copy();\n        }\n\n        // If the data is still not in the cache, insert it\n        data = GetSomeDataToCache();\n        HttpRuntime.Cache.Insert(cacheKey, data, null, DateTime.Today.AddDays(1), Cache.NoSlidingExpiration);\n        return data.Copy();\n    }\n}