using System;\nusing System.Web;\nusing System.Net;\n\npublic class HtmlSender\n{\n    public void SendHtml(string htmlCode, string recipientUrl)\n    {\n        // HTML Encode the HTML code to prevent XSS\n        string encodedHtml = HttpUtility.HtmlEncode(htmlCode);\n\n        // Create a WebClient instance\n        using (WebClient client = new WebClient())\n        {\n            // Set the request headers\n            client.Headers[HttpRequestHeader.ContentType] = "text/html";\n\n            // Send the encoded HTML code to the recipient URL\n            client.UploadString(recipientUrl, encodedHtml);\n        }\n    }\n}