using Google.Apis.Translate.v2;\nusing Google.Apis.Services;\n\n// Create a client instance\nvar service = new TranslateService(new BaseClientService.Initializer\n{\n    ApiKey = "YOUR_API_KEY_HERE",\n    ApplicationName = "YOUR_APPLICATION_NAME_HERE"\n});\n\n// Set the text to translate and the target language\nvar request = new TranslateTextRequest\n{\n    Q = "dog",\n    Target = "fr"\n};\n\n// Execute the translation request\nvar response = service.Translations.List(request).Execute();\n\n// Print the translated text\nConsole.WriteLine(response.Data.Translations[0].TranslatedText);