using System.Xml;\nusing System.Xml.Resolvers;\n\n// Create an instance of XmlPreloadedResolver\nvar resolver = new XmlPreloadedResolver();\n\n// Add the DTD to the resolver's cache\nresolver.Add(new Uri("http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"),\n             new XmlReaderSettings { DtdProcessing = DtdProcessing.Parse },\n             XmlReader.Create(new StringReader(File.ReadAllText("xhtml1-transitional.dtd"))));\n\n// Create an XmlDocument instance and set the resolver\nvar xmlDoc = new XmlDocument();\nxmlDoc.XmlResolver = resolver;\n\n// Load the XHTML document\nxmlDoc.Load("your_xhtml_file.xhtml");