<asp:DropDownList ID="ddlAirports" runat="server" />\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    AirportParser parser = new AirportParser();\n    List<Airport> airports = parser.ParseAirports("path/to/airports.dat");\n\n    ddlAirports.DataSource = airports;\n    ddlAirports.DataTextField = "City";\n    ddlAirports.DataValueField = "IATA";\n    ddlAirports.DataBind();\n}