using System;\nusing System.IO.Pipes;\nusing System.Text;\n\nclass IrbClient\n{\n    public static void Main(string[] args)\n    {\n        using (var client = new NamedPipeClientStream(".", "irb_pipe", PipeDirection.InOut))\n        {\n            client.Connect();\n            var command = Encoding.UTF8.GetBytes("puts \"Hello, World!\"");\n            client.Write(command, 0, command.Length);\n            var buffer = new byte[1024];\n            var bytesRead = client.Read(buffer, 0, buffer.Length);\n            var result = Encoding.UTF8.GetString(buffer, 0, bytesRead);\n            Console.WriteLine(result); // => "Hello, World!\n"\n        }\n    }\n}