using System;\nusing System.Net;\nusing System.IO;\n\npublic class TinyUrlHelper\n{\n    public static string CreateTinyUrl(string originalUrl)\n    {\n        string tinyUrlApi = "http://tinyurl.com/api-create.php?url=" + originalUrl;\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(tinyUrlApi);\n        HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n        using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n        {\n            return reader.ReadToEnd();\n        }\n    }\n}