var query = from items in db.cdsItems\n            where items.ItemHandHeldFlag.Equals("Y")\n            select new\n            {\n                // Other fields you want to select...\n                ProductsQuantity = \n                    (items.ItemPromoFlag != "N") ? 100000 :\n                    (new[] { "1", "2", "31" }.Contains(items.ItemCat1) && items.ItemSaleStatus == "S") ? 100000 :\n                    (items.ItemSaleStatus == "O") ? 0 :\n                    items.ItemQtyOnHand - items.ItemQtyCommitted\n            };