public class TestPage : Page\n{\n    // ...\n\n    protected virtual void WriteIssue(Context context, UserInput input)\n    {\n        // Default implementation: write to database using web.config settings\n        string connectionString = ConfigurationManager.ConnectionStrings["TestDB"].ConnectionString;\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            // Create a command to insert the issue into the database\n            SqlCommand command = new SqlCommand("INSERT INTO Issues (Description, BrowserVersion, ...) VALUES (@Description, @BrowserVersion, ...)", connection);\n            command.Parameters.AddWithValue("@Description", input.Description);\n            command.Parameters.AddWithValue("@BrowserVersion", context.Request.Browser.Type);\n            // ...\n            command.ExecuteNonQuery();\n        }\n    }\n}