using YamlParser;\n\n// assuming 'doc' is the parsed YAML document\nYamlNode rootNode = doc.Root;\n\n// get the first sequence item (the one with the 'id' and 'tag' keys)\nYamlSequenceNode seqNode = (YamlSequenceNode)rootNode.Nodes[0];\n\n// get the 'tag' node\nYamlMappingNode tagNode = (YamlMappingNode)seqNode.Nodes[1];\n\n// get the 'name' node within the 'tag' node\nYamlScalarNode nameNode = (YamlScalarNode)tagNode.Nodes["name"];\n\n// get the value of the 'name' node\nstring nameValue = nameNode.Value;\n\nConsole.WriteLine(nameValue); // outputs "tagname"