[HttpPost]\npublic ActionResult ResetPassword(PasswordResetModel model)\n{\n    if (!ModelState.IsValid)\n    {\n        return View(model);\n    }\n\n    var user = _userRepository.GetUserByEmail(model.Email);\n    if (user == null)\n    {\n        ModelState.AddModelError("", "Email not found");\n        return View(model);\n    }\n\n    var token = GenerateUniqueId(32);\n    var expiresAt = DateTime.Now.AddMinutes(30);\n    var passwordReset = new PasswordResetToken { Token = token, ExpiresAt = expiresAt };\n\n    _passwordResetRepository.CreatePasswordReset(passwordReset);\n\n    // Send email with password reset link\n\n    return RedirectToAction("ResetPasswordSuccess");\n}