using PdfSharp.Pdf;\nusing PdfSharp.Drawing;\n\n// Load the PDF file\nPdfDocument document = PdfReader.Open("path/to/your/pdf/file.pdf", PdfDocumentOpenMode.ReadOnly);\n\n// Create a new bitmap with the desired zoom level\nint zoomLevel = 100; // 100% zoom level\nint width = (int)(document.Pages[0].Width * zoomLevel / 100);\nint height = (int)(document.Pages[0].Height * zoomLevel / 100);\nBitmap bitmap = new Bitmap(width, height);\n\n// Create a graphics object from the bitmap\nGraphics graphics = Graphics.FromImage(bitmap);\n\n// Render the PDF page to the graphics object\ndocument.Pages[0].Draw(graphics, new XRect(0, 0, width, height));\n\n// Dispose of the graphics object and the bitmap\ngraphics.Dispose();\nbitmap.Dispose();\n\n// Display the rendered image\npictureBox1.Image = bitmap;