using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass DigitalSignatureExample\n{\n    public static void Main()\n    {\n        // Create a new RSA key pair\n        using (var rsa = RSA.Create())\n        {\n            // Sign some data with the private key\n            var data = Encoding.UTF8.GetBytes("Hello, World!");\n            var signature = rsa.SignData(data, HashAlgorithmName.SHA256, RSASignaturePadding.Pkcs1);\n\n            // Verify the signature with the public key\n            var publicKey = rsa.ExportParameters(false);\n            using (var rsaVerify = RSA.Create())\n            {\n                rsaVerify.ImportParameters(publicKey);\n                var isValid = rsaVerify.VerifyData(data, signature, HashAlgorithmName.SHA256, RSASignaturePadding.Pkcs1);\n                Console.WriteLine($"Signature is valid: {isValid}");\n            }\n        }\n    }\n}