using iTextSharp.text.pdf;\n\n// Load the PDF file\nPdfReader reader = new PdfReader("path/to/your/pdf.pdf");\n\n// Get the first page\nPdfPage page = reader.GetPage(1);\n\n// Extract text and layout information\nforeach (PdfElement element in page.GetAllElements()) {\n    if (element is PdfPTextBox) {\n        PdfPTextBox textBox = (PdfPTextBox)element;\n        Console.WriteLine($"Text: {textBox.Text}");\n        Console.WriteLine($"X: {textBox.X}, Y: {textBox.Y}, Width: {textBox.Width}, Height: {textBox.Height}");\n    }\n}